import {useState, useEffect} from 'react'

function Reg() {
    let [user, setUser] = useState({
        name: "Иван", 
        surname: "Иванов",
        login: 'ivanov',
        password: '1234',
        email: 'ivanov@mail.ru'
    })

   function add() {
    name = $('#name').val()
    login = $('#login').val()
    let user = {
        name: name, 
        surname: surname,
        login: login,
        password: password,
        email: email
    }
        fetch('http://localhost:5000/api/registration', {
            method: 'POST',
            headers: {
                'Content-Type': 'applicaton/json'
              }, 
            body: JSON.stringify(user)
        })
        .then((res) => res.text())
        .then((res) => {
            console.log(res)
            if (res == 'ok'){
                //вывести "Спасибо за регистрацию"
                $('.ok').html('Спасибо за регистрацию!')
            }
        }
         )

        }
        return (
            <div className="items">



                
                <button onClick={add}>
                    Добавить
                </button>
            </div>
        )
}

export default Reg
