import {  createStore } from "redux"
import { Provider, useSelector, useDispatch } from 'react-redux'

const initialState = {
  name: "Иван"
}

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_NAME": 
    return {
      ...state,
      ...action.payload
    };
    default: 
    return state;
  }
}

const store = createStore(reducer)

const setName = (name) => ({
  type: "ADD_NAME",
  payload: name
})


const App = () => {

  const dispatchc = useDispatch();

  const changeName = () => {
    setName("Петр")
  }

  return <button onClick={changeName}>Изменить</button>
}

const ViewName = () => {

  const storeName = useSelector((state) => state.name)

  return <h1>{storeName}</h1>
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <Provider store={store}>
      <App />
      <ViewName />
    </Provider>
 </React.StrictMode>
);
